import React, { useState } from 'react';
import api from '../../api';
import StarRating from '../common/StarRating';

const ReviewForm = ({ productId, onReviewSubmitted }) => {
    const [rating, setRating] = useState(0);
    const [text, setText] = useState('');
    const [error, setError] = useState('');
    const [submitting, setSubmitting] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (rating === 0) {
            setError('Please select a star rating.');
            return;
        }
        if (!text.trim()) {
            setError('Please write your feedback.');
            return;
        }
        
        setError('');
        setSubmitting(true);

        try {
            const { data } = await api.post('/reviews', { productId, rating, text });
            onReviewSubmitted(data);
            setText('');
            setRating(0);
        } catch (err) {
            setError('Failed to submit review. Please try again.');
            console.error('Failed to submit review:', err);
        } finally {
            setSubmitting(false);
        }
    };

    return (
        <div className="bg-gradient-to-br from-[#1e1e2f] to-[#2a2a3e] border border-slate-700 rounded-xl p-6 shadow-2xl text-slate-200 mb-8">
            <h3 className="text-center text-white text-xl font-bold mb-5">Share Your Experience</h3>
            
            {error && <p className="text-red-500 text-center mb-4">{error}</p>}
            
            <form onSubmit={handleSubmit}>
                <div className="mb-4 text-center">
                    <label className="block mb-2 text-slate-300">Your Rating:</label>
                    <StarRating rating={rating} onRating={(rate) => setRating(rate)} />
                </div>
                
                <div className="mb-4">
                    <label htmlFor="reviewText" className="block mb-2 text-slate-300">Your Feedback:</label>
                    <textarea
                        id="reviewText"
                        value={text}
                        onChange={(e) => setText(e.target.value)}
                        placeholder="What did you like or dislike?"
                        className="w-full bg-[#1e1e2f] border border-slate-700 text-slate-200 rounded-lg p-3 min-h-[100px] focus:ring-2 focus:ring-purple-500 focus:border-purple-500 transition"
                        required
                    />
                </div>
                
                <div className="text-center">
                    <button 
                        type="submit" 
                        disabled={submitting} 
                        className="bg-gradient-to-r from-purple-600 to-indigo-700 text-white font-bold py-3 px-8 rounded-full shadow-lg hover:shadow-purple-500/50 transform hover:scale-105 transition-all duration-300 ease-in-out disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                        {submitting ? 'Submitting...' : 'Post Review'}
                    </button>
                </div>
            </form>
        </div>
    );
};

export default ReviewForm;
