import React, { useState, useEffect } from 'react';
import api from '../api';
import StatsCharts from '../components/AdminDashboard/StatsCharts';
import ReviewList from '../components/AdminDashboard/ReviewList';

const AdminPage = () => {
    const [reviews, setReviews] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchReviews = async () => {
            try {
                const { data } = await api.get('/reviews');
                setReviews(data);
            } catch (error) {
                setError("Failed to fetch reviews. You may not have admin privileges.");
                console.error("Failed to fetch reviews", error);
            } finally {
                setLoading(false);
            }
        };
        fetchReviews();
    }, []);

    if (loading) return <p className="text-center text-slate-400 mt-10">Loading Dashboard...</p>;
    if (error) return <p className="text-center text-red-500 mt-10">{error}</p>;

    return (
        <div className="container mx-auto p-4 text-black">
            <h1 className="text-4xl font-bold border-b-2 border-purple-500 pb-3 mb-8">Admin Dashboard</h1>
            
            {/* The StatsCharts component would also need to be refactored, but for now it will sit here */}
            {/* <StatsCharts reviews={reviews} /> */}

            <div className="mt-8">
                <ReviewList reviews={reviews} />
            </div>
        </div>
    );
};

export default AdminPage;
