import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from '../api';
import ReviewForm from '../components/Product/ReviewForm';
import StarRating from '../components/common/StarRating';
import { useAuth } from '../context/AuthContext'; // Corrected casing

const ProductDetailPage = () => {
    const { id } = useParams();
    const { isAuthenticated, user } = useAuth(); // Call useAuth at the top level
    const [product, setProduct] = useState(null);
    const [reviews, setReviews] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchProductAndReviews = async () => {
            try {
                setLoading(true);
                const productRes = await api.get(`/products/${id}`);
                setProduct(productRes.data);

                const reviewsRes = await api.get(`/reviews/product/${id}`);
                setReviews(reviewsRes.data);
            } catch (err) {
                setError('Failed to load product details.');
                console.error(err);
            } finally {
                setLoading(false);
            }
        };
        fetchProductAndReviews();
    }, [id]);

    const handleReviewSubmitted = (newReview) => {
        // user is now available here from the top-level hook call
        const reviewWithUser = { ...newReview, user: { name: user.name } };
        setReviews([reviewWithUser, ...reviews]);
    };

    if (loading) return <p>Loading product details...</p>;
    if (error) return <p style={{ color: 'red' }}>{error}</p>;
    if (!product) return <p>Product not found.</p>;

    return (
        <div>
            <div className="product-detail-header" style={{ display: 'flex', alignItems: 'center', gap: '2rem', marginBottom: '2rem' }}>
                <img src={product.imageUrl} alt={product.name} style={{ width: '150px', height: '150px', borderRadius: '5px', objectFit: 'cover' }} />
                <div>
                    <h1>{product.name}</h1>
                    <p>{product.description}</p>
                </div>
            </div>

            <div className="reviews-section">
                <h2>Customer Reviews</h2>
                {isAuthenticated ? (
                    <ReviewForm productId={id} onReviewSubmitted={handleReviewSubmitted} />
                ) : (
                    <p>Please log in to leave a review.</p>
                )}

                <div className="review-list">
                    {reviews.length > 0 ? (
                        reviews.map((review) => (
                            <div key={review._id} className="review-item">
                                <strong>{review.user?.name || 'Anonymous'}</strong>
                                <div className="review-meta">
                                    <StarRating rating={review.rating} />
                                    <span> on {new Date(review.createdAt).toLocaleDateString()}</span>
                                </div>
                                <p>{review.text}</p>
                            </div>
                        ))
                    ) : (
                        <p>No reviews yet. Be the first to write one!</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default ProductDetailPage;
