import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from '../api';
import { useAuth } from '../context/AuthContext';
import StarRating from '../components/common/StarRating';
import ReviewForm from '../components/Product/ReviewForm';

// --- NEW: Futuristic ReviewItem Component with Tailwind CSS ---
const ReviewItem = ({ review, onReviewDeleted, onReviewUpdated }) => {
    const { user } = useAuth();
    const [isEditing, setIsEditing] = useState(false);
    const [editText, setEditText] = useState(review.text);
    const [editRating, setEditRating] = useState(review.rating);

    // Check if the currently logged-in user is the author of the review
    const isAuthor = user && user.id === review.user._id;

    const handleDelete = async () => {
        // A simple confirmation dialog before deleting
        if (window.confirm('Are you sure you want to delete this review?')) {
            try {
                await api.delete(`/reviews/${review._id}`);
                onReviewDeleted(review._id);
            } catch (error) {
                console.error('Failed to delete review:', error);
                alert('Could not delete the review. Please try again.');
            }
        }
    };

    const handleUpdate = async (e) => {
        e.preventDefault();
        try {
            const { data: updatedReview } = await api.put(`/reviews/${review._id}`, {
                text: editText,
                rating: editRating,
            });
            onReviewUpdated(updatedReview);
            setIsEditing(false); // Close the edit form on success
        } catch (error) {
            console.error('Failed to update review:', error);
            alert('Could not update the review. Please try again.');
        }
    };

    return (
        <div className="bg-gradient-to-br from-[#1e1e2f] to-[#2a2a3e] border border-slate-700 rounded-xl p-5 mb-5 shadow-lg relative text-slate-300">
            {isAuthor && (
                <div className="absolute top-4 right-4 flex gap-2">
                    <button 
                        onClick={() => setIsEditing(!isEditing)} 
                        className="bg-transparent border border-purple-500 text-purple-400 text-xs font-bold py-1 px-3 rounded-full hover:bg-purple-500 hover:text-white transition-all duration-300"
                    >
                        {isEditing ? 'Cancel' : 'Edit'}
                    </button>
                    <button 
                        onClick={handleDelete} 
                        className="bg-transparent border border-red-500 text-red-400 text-xs font-bold py-1 px-3 rounded-full hover:bg-red-500 hover:text-white transition-all duration-300"
                    >
                        Delete
                    </button>
                </div>
            )}

            <div className="flex items-center mb-2">
                <strong className="text-white text-lg">{review.user?.name || 'Anonymous'}</strong>
            </div>

            {isEditing ? (
                <form onSubmit={handleUpdate} className="mt-4">
                    <div className="mb-3">
                        <StarRating rating={editRating} onRating={setEditRating} />
                    </div>
                    <textarea 
                        value={editText} 
                        onChange={(e) => setEditText(e.target.value)}
                        className="w-full bg-[#2a2a3e] border border-slate-600 text-slate-200 rounded-lg p-2 focus:ring-2 focus:ring-purple-500 transition"
                    />
                    <button 
                        type="submit" 
                        className="mt-3 bg-purple-600 text-white font-bold py-2 px-5 rounded-full hover:bg-purple-700 transition-all duration-300"
                    >
                        Save Changes
                    </button>
                </form>
            ) : (
                <div>
                    <StarRating rating={review.rating} />
                    <p className="leading-relaxed my-3">{review.text}</p>
                    {review.adminReply?.text && (
                        <div className="bg-slate-800/50 border-l-4 border-purple-500 rounded-r-lg p-4 mt-4 italic">
                            <strong className="text-purple-400">Reply from Admin:</strong>
                            <p className="mt-1 text-slate-400">{review.adminReply.text}</p>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};


// --- UPDATED: ProductDetailPage with Tailwind CSS ---
const ProductDetailPage = () => {
    const { id } = useParams();
    const { isAuthenticated } = useAuth();
    const [product, setProduct] = useState(null);
    const [reviews, setReviews] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchProductAndReviews = async () => {
            try {
                setLoading(true);
                const [productRes, reviewsRes] = await Promise.all([
                    api.get(`/products/${id}`),
                    api.get(`/reviews/product/${id}`)
                ]);
                setProduct(productRes.data);
                setReviews(reviewsRes.data);
            } catch (err) {
                console.error('Failed to load product details:', err);
            } finally {
                setLoading(false);
            }
        };
        fetchProductAndReviews();
    }, [id]);

    const handleReviewSubmitted = (newReview) => {
        setReviews([newReview, ...reviews]);
    };

    const handleReviewDeleted = (reviewId) => {
        setReviews(reviews.filter(r => r._id !== reviewId));
    };

    const handleReviewUpdated = (updatedReview) => {
        setReviews(reviews.map(r => r._id === updatedReview._id ? updatedReview : r));
    };

    if (loading) return <p className="text-center text-slate-400">Loading...</p>;
    if (!product) return <p className="text-center text-red-500">Product not found.</p>;

    return (
        <div className="container mx-auto p-4">
            <div className="mb-10 text-white">
                 <h1 className="text-4xl font-bold mb-2">{product.name}</h1>
                 <p className="text-slate-400 text-lg">{product.description}</p>
            </div>

            <div className="reviews-section">
                <h2 className="text-3xl font-bold text-white border-b-2 border-purple-500 pb-3 mb-6">Customer Reviews</h2>
                
                {isAuthenticated && (
                    <div className="my-6">
                        <ReviewForm productId={id} onReviewSubmitted={handleReviewSubmitted} />
                    </div>
                )}

                <div className="review-list">
                    {reviews.length > 0 ? (
                        reviews.map((review) => (
                            <ReviewItem 
                                key={review._id} 
                                review={review} 
                                onReviewDeleted={handleReviewDeleted}
                                onReviewUpdated={handleReviewUpdated}
                            />
                        ))
                    ) : (
                        <p className="text-slate-500">No reviews yet. Be the first to write one!</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default ProductDetailPage;
