import React, { useState, useMemo } from 'react';

// A small helper component to style the topic tags
const TopicTag = ({ topic }) => {
    const sentimentColors = {
        Positive: 'bg-green-500/20 text-green-300 border border-green-500/30',
        Negative: 'bg-red-500/20 text-red-300 border border-red-500/30',
        Neutral: 'bg-slate-500/20 text-slate-300 border border-slate-500/30',
    };

    return (
        <span className={`py-1 px-3 rounded-full text-xs font-medium ${sentimentColors[topic.sentiment] || 'bg-blue-500/20 text-blue-300'}`}>
            {topic.topic}
        </span>
    );
};


const ReviewList = ({ reviews = [] }) => {
    const [filterProduct, setFilterProduct] = useState('');
    const [filterSentiment, setFilterSentiment] = useState('');

    const uniqueProducts = useMemo(() => {
        const productNames = reviews.map(r => r.product?.name).filter(Boolean);
        return [...new Set(productNames)];
    }, [reviews]);

    const filteredReviews = useMemo(() => {
        return reviews.filter(review => {
            const productMatch = filterProduct ? review.product?.name === filterProduct : true;
            const sentimentMatch = filterSentiment ? (review.sentiment === filterSentiment || (filterSentiment === 'Unanalyzed' && review.sentiment === null)) : true;
            return productMatch && sentimentMatch;
        });
    }, [reviews, filterProduct, filterSentiment]);

    return (
        <div className="bg-gradient-to-br from-[#1e1e2f] to-[#2a2a3e] border border-slate-700 rounded-xl p-6 shadow-2xl">
            <div className="flex flex-col sm:flex-row justify-between items-center mb-5 gap-4">
                <h3 className="text-xl font-bold text-white">All Customer Reviews</h3>
                <div className="flex gap-4">
                    <select value={filterProduct} onChange={e => setFilterProduct(e.target.value)} className="bg-[#2a2a3e] border border-slate-600 rounded-md px-3 py-2 text-white focus:ring-2 focus:ring-purple-500">
                        <option value="">All Products</option>
                        {uniqueProducts.map(name => <option key={name} value={name}>{name}</option>)}
                    </select>
                    <select value={filterSentiment} onChange={e => setFilterSentiment(e.target.value)} className="bg-[#2a2a3e] border border-slate-600 rounded-md px-3 py-2 text-white focus:ring-2 focus:ring-purple-500">
                        <option value="">All Sentiments</option>
                        <option value="Positive">Positive</option>
                        <option value="Negative">Negative</option>
                        <option value="Neutral">Neutral</option>
                        <option value="Unanalyzed">Unanalyzed</option>
                    </select>
                </div>
            </div>
            
            <div className="overflow-x-auto">
                <table className="w-full text-left text-slate-300">
                    <thead className="bg-slate-800/50 text-xs text-slate-400 uppercase">
                        <tr>
                            <th scope="col" className="px-6 py-3">Product</th>
                            <th scope="col" className="px-6 py-3">User</th>
                            <th scope="col" className="px-6 py-3">Rating</th>
                            <th scope="col" className="px-6 py-3 min-w-[250px]">Review Text</th>
                            <th scope="col" className="px-6 py-3">Overall Sentiment</th>
                            <th scope="col" className="px-6 py-3">AI Topics</th>
                            <th scope="col" className="px-6 py-3">Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredReviews.map(review => (
                            <tr key={review._id} className="border-b border-slate-700 hover:bg-slate-800/40">
                                <td className="px-6 py-4 font-medium text-white">{review.product?.name || 'N/A'}</td>
                                <td className="px-6 py-4">{review.user?.name || 'N/A'}</td>
                                <td className="px-6 py-4 text-yellow-400">{review.rating} ★</td>
                                <td className="px-6 py-4">{review.text}</td>
                                <td className="px-6 py-4">
                                    <span className={`font-semibold ${
                                        review.sentiment === 'Positive' ? 'text-green-400' :
                                        review.sentiment === 'Negative' ? 'text-red-400' : 'text-slate-400'
                                    }`}>
                                        {review.sentiment || 'N/A'}
                                    </span>
                                </td>
                                <td className="px-6 py-4">
                                    <div className="flex flex-wrap gap-2">
                                        {review.topics && review.topics.length > 0
                                            ? review.topics.map((topic, index) => <TopicTag key={index} topic={topic} />)
                                            : 'N/A'}
                                    </div>
                                </td>
                                <td className="px-6 py-4">{new Date(review.createdAt).toLocaleDateString()}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default ReviewList;
